mapper.update:
update user
set user_login_name = #{userLoginName,jdbcType=VARCHAR},
  user_password = #{userPassword,jdbcType=VARCHAR},
  user_email = #{userEmail,jdbcType=VARCHAR},
  user_photo = #{userPhoto,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select user_id, user_login_name, user_password, user_email, user_photo
from user
where user_id = #{userId,jdbcType=INTEGER}

select user_id, user_login_name, user_password, user_email, user_photo
from user
where user_login_name = #{userName,jdbcType=VARCHAR}

select user_id, user_login_name, user_password, user_email, user_photo
from user
where user_email = #{email,jdbcType=VARCHAR}
